css:
.print-format {
padding: 0.25in;
}

tr.item:not(:last-child) > td {border-bottom: 1px solid;}

@media print {
.print-format {
padding: 0.25in;
}
}

.po-cell {
    border: 1px solid;
    border-radius: 5px;
    text-align: center;
    vertical-align: top;
}

.po-cell > h4 {
    border-bottom: 1px solid;
    margin: 0px;
    padding: 5px;
}

body {
counter-reset: page 1;
page: main;
}

.page-counter { content: "Page " counter(page) " of " counter(pages) }


@page {

    margin: 2.0in 0.25in 0.25in 0.25in;

    @top-left {
        content: flow(header)
    }
}

header {flow: static(header)}
